<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class Login_Model extends CI_Model {    function Login_Model() {        parent::__construct();    }    function check_login($email, $password) {        $md5password = md5($password);        $result = $this->db->get_where('mo_websiteuseragents', array('email' => $email, 'password' => $md5password));        return $result->result_array();    }    function confirm_registration($activationNo) {        $val_code = "SELECT sn from mo_websiteuseragents where activationNo = ?";        $result = $this->db->query($val_code, $activationNo);        if ($result->num_rows() == 1) {            $update_activated = "UPDATE `mo_websiteuseragents` SET emailApproval = 'Y' WHERE activationNo = ?";            $this->db->query($update_activated, $activationNo);            return TRUE;        } else {            return FALSE;        }    }}